#include <cstdint>
#include <set>
#include <unordered_map>
typedef uint64_t obj_id_t;

// Enhanced LFU: Also consider object size in tiebreaker, prefer to evict larger objects with same freq/recency.
class CacheManager {
private:
    struct LFUNode {
        uint64_t freq;
        uint64_t timestamp; // for tiebreak
        uint64_t obj_size;
        obj_id_t obj_id;
        LFUNode(uint64_t f, uint64_t ts, uint64_t sz, obj_id_t id)
            : freq(f), timestamp(ts), obj_size(sz), obj_id(id) {}
        // Set ordering: freq first, then timestamp, then size (prefer to keep smaller hot objects), then obj_id
        bool operator<(const LFUNode &o) const {
            if (freq != o.freq) return freq < o.freq;
            if (timestamp != o.timestamp) return timestamp < o.timestamp;
            if (obj_size != o.obj_size) return obj_size > o.obj_size; // prefer to evict larger for same freq
            return obj_id < o.obj_id;
        }
    };

    // obj_id -> {size, {node, iterator in set}}
    std::unordered_map<obj_id_t, std::pair<uint64_t, std::set<LFUNode>::iterator>> map;
    std::set<LFUNode> lfu_set;
    uint64_t time_counter = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = map.find(obj_id);
        if (it == map.end()) return false;
        uint64_t obj_size = it->second.first;
        auto set_it = it->second.second;
        LFUNode node = *set_it;
        lfu_set.erase(set_it);
        node.freq += 1;
        node.timestamp = ++time_counter;
        auto iter = lfu_set.insert(LFUNode(node.freq, node.timestamp, obj_size, obj_id)).first;
        it->second.second = iter;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = map.find(obj_id);
        if (it != map.end()) {
            find(obj_id);
            return;
        }
        LFUNode node(1, ++time_counter, obj_size, obj_id);
        auto iter = lfu_set.insert(node).first;
        map[obj_id] = std::make_pair(obj_size, iter);
    }

    obj_id_t evict() {
        if (lfu_set.empty()) return 0;
        auto it = lfu_set.begin();
        obj_id_t victim = it->obj_id;
        map.erase(victim);
        lfu_set.erase(it);
        return victim;
    }
};
