#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

class CacheManager {
private:
    // LFU with size-awareness: track frequency and size for each object.
    struct CacheObject {
        uint64_t freq;
        uint64_t size;
        uint64_t timestamp;
    };
    struct LFUNode {
        double priority;
        uint64_t timestamp;
        obj_id_t id;
        bool operator<(const LFUNode& o) const {
            if (priority != o.priority) return priority < o.priority;
            return timestamp < o.timestamp;
        }
    };

    std::unordered_map<obj_id_t, CacheObject> cache_map;
    std::set<LFUNode> priority_set;
    uint64_t counter = 0;
    double last_evict_pri = 0.0;

    double calc_pri(uint64_t freq, uint64_t size) {
        if (size == 0) return last_evict_pri + freq * 1e6;
        return last_evict_pri + double(freq) * 1e6 / size;
    }

public:
    bool find(obj_id_t obj_id) {
        auto it = cache_map.find(obj_id);
        if (it == cache_map.end()) return false;
        // Erase from set
        double old_pri = calc_pri(it->second.freq, it->second.size);
        LFUNode node{old_pri, it->second.timestamp, obj_id};
        priority_set.erase(node);
        it->second.freq += 1;
        it->second.timestamp = ++counter;
        double new_pri = calc_pri(it->second.freq, it->second.size);
        priority_set.insert({new_pri, it->second.timestamp, obj_id});
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = cache_map.find(obj_id);
        if (it != cache_map.end()) {
            find(obj_id);
            return;
        }
        uint64_t freq = 1;
        uint64_t ts = ++counter;
        double pri = calc_pri(freq, obj_size);
        cache_map[obj_id] = {freq, obj_size, ts};
        priority_set.insert({pri, ts, obj_id});
    }

    obj_id_t evict() {
        if (priority_set.empty()) return 0;
        auto it = priority_set.begin();
        obj_id_t victim = it->id;
        last_evict_pri = it->priority;
        priority_set.erase(it);
        cache_map.erase(victim);
        return victim;
    }
};
