#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// Implement an LFU (Least Frequently Used) cache with LRU tiebreaker for diversity and improved fitness
class CacheManager {
private:
    struct Node {
        uint64_t freq;
        uint64_t timestamp;
        obj_id_t id;
        bool operator<(const Node& other) const {
            if (freq != other.freq) return freq < other.freq;
            if (timestamp != other.timestamp) return timestamp < other.timestamp;
            return id < other.id;
        }
    };
    std::unordered_map<obj_id_t, std::pair<uint64_t, std::set<Node>::iterator>> obj2node;
    std::set<Node> freq_set;
    uint64_t ts = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2node.find(obj_id);
        if (it == obj2node.end()) return false;
        Node old_node = *(it->second.second);
        freq_set.erase(it->second.second);
        Node new_node = {old_node.freq + 1, ++ts, obj_id};
        auto iter = freq_set.insert(new_node).first;
        it->second = {new_node.freq, iter};
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        (void)obj_size;
        auto it = obj2node.find(obj_id);
        if (it != obj2node.end()) {
            find(obj_id);
            return;
        }
        Node n = {1, ++ts, obj_id};
        auto iter = freq_set.insert(n).first;
        obj2node[obj_id] = {1, iter};
    }

    obj_id_t evict() {
        if (freq_set.empty()) return 0;
        auto it = freq_set.begin();
        obj_id_t victim = it->id;
        freq_set.erase(it);
        obj2node.erase(victim);
        return victim;
    }
};
