#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// LFU+Size-Aware: Uses frequency, but for ties, prefers to evict LARGER objects (so we keep small hot objects).
// This variant adds a "recently evicted priority" (GDSF L value) to the mix for more adaptation to changing workloads.
class CacheManager {
private:
    struct LFUNode {
        double priority;   // Priority value for eviction ordering (incorporates freq and size with L aging)
        uint64_t timestamp;
        uint64_t freq;
        uint64_t size;
        obj_id_t obj_id;
        LFUNode(double pri, uint64_t ts, uint64_t f, uint64_t s, obj_id_t id)
            : priority(pri), timestamp(ts), freq(f), size(s), obj_id(id) {}
        // Lower priority first. Break ties by timestamp, then by obj_id.
        bool operator<(const LFUNode &o) const {
            if (priority != o.priority) return priority < o.priority;
            if (timestamp != o.timestamp) return timestamp < o.timestamp;
            return obj_id < o.obj_id;
        }
    };

    std::unordered_map<obj_id_t, std::set<LFUNode>::iterator> obj2iter;
    std::unordered_map<obj_id_t, uint64_t> obj2size;
    std::unordered_map<obj_id_t, uint64_t> obj2freq;
    std::set<LFUNode> lfu_set;
    uint64_t time_counter = 0;
    double last_evict_pri = 0.0;

    double calc_priority(uint64_t freq, uint64_t size) {
        // GDSF-inspired: L + freq / size (scaled)
        if (size == 0) return last_evict_pri + freq * 1e6;
        return last_evict_pri + (double(freq) * 1e6 / double(size));
    }

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2iter.find(obj_id);
        if (it == obj2iter.end()) return false;
        auto node = *(it->second);
        lfu_set.erase(it->second);

        uint64_t new_freq = node.freq + 1;
        obj2freq[obj_id] = new_freq;
        double new_pri = calc_priority(new_freq, node.size);
        auto iter = lfu_set.insert(LFUNode(new_pri, ++time_counter, new_freq, node.size, node.obj_id)).first;
        obj2iter[obj_id] = iter;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2iter.find(obj_id);
        if (it != obj2iter.end()) {
            find(obj_id);
            return;
        }
        obj2size[obj_id] = obj_size;
        obj2freq[obj_id] = 1;
        double pri = calc_priority(1, obj_size);
        LFUNode node(pri, ++time_counter, 1, obj_size, obj_id);
        auto iter = lfu_set.insert(node).first;
        obj2iter[obj_id] = iter;
    }

    obj_id_t evict() {
        if (lfu_set.empty()) return 0;
        auto it = lfu_set.begin();
        obj_id_t victim = it->obj_id;
        last_evict_pri = it->priority;
        lfu_set.erase(it);
        obj2iter.erase(victim);
        obj2size.erase(victim);
        obj2freq.erase(victim);
        return victim;
    }
};
