#include <cstdint>
#include <unordered_map>
#include <set>
#include <vector>

typedef uint64_t obj_id_t;

// GDSF-inspired hybrid: frequency/size, small object bias, recency tiebreak
class CacheManager {
private:
    struct Node {
        double priority;
        uint64_t timestamp;
        uint64_t freq;
        uint64_t size;
        obj_id_t id;
        Node(double p, uint64_t ts, uint64_t f, uint64_t s, obj_id_t i)
            : priority(p), timestamp(ts), freq(f), size(s), id(i) {}
        bool operator<(const Node &o) const {
            if (priority != o.priority) return priority < o.priority;
            return timestamp < o.timestamp;
        }
    };

    std::set<Node> pq;
    std::unordered_map<obj_id_t, std::set<Node>::iterator> obj2it;
    std::unordered_map<obj_id_t, uint64_t> obj2size;
    double last_evict_pri = 0.0;
    uint64_t counter = 0;

    double calc_priority(uint64_t freq, uint64_t size) {
        // GDSF: L + freq*1e6/size, favor smalls and recency
        // Smaller small_bonus, remove frequency decay for stability.
        double small_bonus = (size <= 8) ? 1.0 : 0.0;
        if (size == 0) return last_evict_pri + (freq + small_bonus) * 1e6 + counter * 1e-4;
        return last_evict_pri + (freq + small_bonus) * 1e6 / size + counter * 1e-4;
    }

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2it.find(obj_id);
        if (it == obj2it.end()) return false;
        Node node = *(it->second);
        pq.erase(it->second);
        node.freq += (node.size <= 8) ? 2 : 1; // boost small objects' freq further on hit
        node.timestamp = ++counter;
        node.priority = calc_priority(node.freq, node.size);
        auto iter = pq.insert(node).first;
        obj2it[obj_id] = iter;
        obj2size[obj_id] = node.size;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2it.find(obj_id);
        if (it != obj2it.end()) {
            find(obj_id);
            return;
        }
        uint64_t init_freq = (obj_size <= 8) ? 2 : 1; // small objects start hotter
        Node node(calc_priority(init_freq, obj_size), ++counter, init_freq, obj_size, obj_id);
        auto iter = pq.insert(node).first;
        obj2it[obj_id] = iter;
        obj2size[obj_id] = obj_size;
    }

    obj_id_t evict() {
        if (pq.empty()) return 0;
        auto it = pq.begin();
        obj_id_t victim = it->id;
        last_evict_pri = it->priority;

        pq.erase(it);
        obj2it.erase(victim);
        obj2size.erase(victim);
        return victim;
    }
};
