#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

/*
 * LFU+Size-Aware: Uses frequency, but for ties, prefers to evict LARGER objects (so we keep small hot objects).
 * Motivation: The plain LFU algorithm in the original code can be improved by using object size as a tiebreaker,
 * keeping small hot objects and evicting larger ones on equal frequency. This helps reduce byte miss ratio,
 * improving fitness, especially for byte-weighted workloads.
 */
class CacheManager {
private:
    struct Node {
        uint64_t freq;
        uint64_t timestamp;
        uint64_t obj_size;
        obj_id_t obj_id;
        bool operator<(const Node& rhs) const {
            if (freq != rhs.freq) return freq < rhs.freq;
            if (obj_size != rhs.obj_size) return obj_size > rhs.obj_size; // prefer to evict larger object on freq tie
            if (timestamp != rhs.timestamp) return timestamp < rhs.timestamp;
            return obj_id < rhs.obj_id;
        }
    };

    std::unordered_map<obj_id_t, std::set<Node>::iterator> obj2iter;
    std::set<Node> nodes;
    uint64_t timestamp = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2iter.find(obj_id);
        if (it == obj2iter.end()) return false;
        Node node = *(it->second);
        nodes.erase(it->second);
        node.freq += 1;
        node.timestamp = ++timestamp;
        auto new_it = nodes.insert(node).first;
        obj2iter[obj_id] = new_it;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2iter.find(obj_id);
        if (it != obj2iter.end()) {
            find(obj_id);
            return;
        }
        Node node{1, ++timestamp, obj_size, obj_id};
        auto set_it = nodes.insert(node).first;
        obj2iter[obj_id] = set_it;
    }

    obj_id_t evict() {
        if (nodes.empty()) return 0;
        auto it = nodes.begin();
        obj_id_t victim = it->obj_id;
        nodes.erase(it);
        obj2iter.erase(victim);
        return victim;
    }
};
