#include <cstdint>
#include <unordered_map>
#include <set>
#include <vector>

typedef uint64_t obj_id_t;

/*
 * LFU+Size-Aware: Uses frequency, but for ties, prefers to evict LARGER objects (so we keep small hot objects).
 * Enhancement: Add lightweight frequency aging on eviction (decay freq counts on ALL entries),
 * which helps avoid stale objects being protected forever, boosting recency and adaptability.
 */
class CacheManager {
private:
    struct LFUNode {
        uint64_t freq;
        uint64_t timestamp; // for LRU tiebreak
        uint64_t size;
        obj_id_t obj_id;
        LFUNode(uint64_t f, uint64_t ts, uint64_t s, obj_id_t id) : freq(f), timestamp(ts), size(s), obj_id(id) {}
        // Set ordering: freq, then size descending (evict big on tie), then timestamp, then obj_id
        bool operator<(const LFUNode &o) const {
            if (freq != o.freq) return freq < o.freq;
            if (size != o.size) return size > o.size; // prefer to evict bigger object on tie
            if (timestamp != o.timestamp) return timestamp < o.timestamp;
            return obj_id < o.obj_id;
        }
    };

    std::unordered_map<obj_id_t, std::set<LFUNode>::iterator> obj2iter;
    std::set<LFUNode> lfu_set;
    std::unordered_map<obj_id_t, uint64_t> obj2size;
    uint64_t time_counter = 0;

    // Frequency aging: After each eviction, decay (halve) all frequencies > 1 for all objects.
    // MODIFIED: Only age frequencies for objects with frequency >= 4 and only if set is "large"
    //           to reduce churn and preserve hot objects when cache is small.
    void age_all_frequencies() {
        constexpr uint64_t k_min_age_freq = 4;
        constexpr size_t k_age_threshold = 32;
        if (lfu_set.size() < k_age_threshold) return;
        std::vector<LFUNode> updated_nodes;
        for (auto it = lfu_set.begin(); it != lfu_set.end(); ++it) {
            LFUNode n = *it;
            if (n.freq >= k_min_age_freq) n.freq = (n.freq + 1) / 2;
            updated_nodes.push_back(n);
        }
        lfu_set.clear();
        obj2iter.clear();
        for (const auto& n : updated_nodes) {
            auto iter = lfu_set.insert(n).first;
            obj2iter[n.obj_id] = iter;
            obj2size[n.obj_id] = n.size;
        }
    }

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2iter.find(obj_id);
        if (it == obj2iter.end()) return false;
        auto node = *(it->second);
        lfu_set.erase(it->second);
        node.freq += 1;
        node.timestamp = ++time_counter;
        auto iter = lfu_set.insert(LFUNode(node.freq, node.timestamp, node.size, node.obj_id)).first;
        obj2iter[obj_id] = iter;

        // Promote "aging" occasionally even on find, to prevent staleness in ultra-hot cache
        // (Very light touch: once per 4096th access, do a quick pass for slight freq decay if size is large)
        static uint64_t find_access_counter = 0;
        if ((++find_access_counter & 0xFFF) == 0 && lfu_set.size() > 128) {
            std::vector<LFUNode> aged_nodes;
            for (const auto& n : lfu_set) {
                LFUNode m = n;
                if (m.freq >= 32) m.freq = (m.freq + 1) / 2;
                aged_nodes.push_back(m);
            }
            lfu_set.clear();
            obj2iter.clear();
            for (const auto& n : aged_nodes) {
                auto it2 = lfu_set.insert(n).first;
                obj2iter[n.obj_id] = it2;
            }
        }

        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2iter.find(obj_id);
        if (it != obj2iter.end()) {
            find(obj_id);
            return;
        }
        LFUNode node(1, ++time_counter, obj_size, obj_id);
        auto iter = lfu_set.insert(node).first;
        obj2iter[obj_id] = iter;
        obj2size[obj_id] = obj_size;
    }

    obj_id_t evict() {
        if (lfu_set.empty()) return 0;
        auto it = lfu_set.begin();
        obj_id_t victim = it->obj_id;
        lfu_set.erase(it);
        obj2iter.erase(victim);
        obj2size.erase(victim);
        age_all_frequencies(); // only decay if set is large and entry was long-lived
        return victim;
    }
};
