#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

/*
LFU with size-aware tiebreaker: On freq ties, evict the LARGER object (keeps small hot items).
This can improve byte-miss and overall fitness on mixed-object-size traces.
*/
class CacheManager {
private:
    struct Node {
        uint64_t freq;
        uint64_t timestamp;
        uint64_t size;
        obj_id_t id;
        bool operator<(const Node& other) const {
            if (freq != other.freq) return freq < other.freq;
            if (size != other.size) return size > other.size; // evict larger size on freq tie
            if (timestamp != other.timestamp) return timestamp < other.timestamp;
            return id < other.id;
        }
    };
    std::unordered_map<obj_id_t, std::set<Node>::iterator> obj2node;
    std::unordered_map<obj_id_t, uint64_t> obj2size;
    std::set<Node> freq_set;
    uint64_t ts = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2node.find(obj_id);
        if (it == obj2node.end()) return false;
        Node old_node = *(it->second);
        freq_set.erase(it->second);
        Node new_node = {old_node.freq + 1, ++ts, old_node.size, obj_id};
        auto iter = freq_set.insert(new_node).first;
        obj2node[obj_id] = iter;
        obj2size[obj_id] = old_node.size;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2node.find(obj_id);
        if (it != obj2node.end()) {
            find(obj_id);
            return;
        }
        Node n = {1, ++ts, obj_size, obj_id};
        auto iter = freq_set.insert(n).first;
        obj2node[obj_id] = iter;
        obj2size[obj_id] = obj_size;
    }

    obj_id_t evict() {
        if (freq_set.empty()) return 0;
        auto it = freq_set.begin();
        obj_id_t victim = it->id;
        freq_set.erase(it);
        obj2node.erase(victim);
        obj2size.erase(victim);
        return victim;
    }
};
