#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// Weighted LFU: evicts by lowest freq, oldest time, and highest size/lowest size ratio (prefer to keep small, highly referenced items). 
// Upon access, frequency increases, time updates to improve recency.
class CacheManager {
private:
    struct Node {
        obj_id_t id;
        uint64_t freq;
        uint64_t time;
        uint64_t size;
        Node(obj_id_t i, uint64_t f, uint64_t t, uint64_t s) : id(i), freq(f), time(t), size(s) {}
        // Use freq/size ratio as primary key, then recency, then size for tiebreaking
        bool operator<(const Node& o) const {
            double myScore = size == 0 ? freq : (double)freq / size;
            double oScore = o.size == 0 ? o.freq : (double)o.freq / o.size;
            if (myScore != oScore) return myScore < oScore;
            if (time != o.time) return time < o.time; // older first
            if (size != o.size) return size > o.size; // keep small items
            return id < o.id;
        }
    };
    std::unordered_map<obj_id_t, std::set<Node>::iterator> lookup;
    std::set<Node> freq_set;
    uint64_t tick = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = lookup.find(obj_id);
        if (it == lookup.end()) return false;
        Node old_node = *(it->second);
        freq_set.erase(it->second);
        Node upd(old_node.id, old_node.freq+1, ++tick, old_node.size);
        auto iter = freq_set.insert(upd).first;
        lookup[obj_id] = iter;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        if (lookup.count(obj_id)) {
            find(obj_id);
            return;
        }
        Node n(obj_id, 1, ++tick, obj_size);
        auto iter = freq_set.insert(n).first;
        lookup[obj_id] = iter;
    }

    obj_id_t evict() {
        if (freq_set.empty()) return 0;
        auto victim = freq_set.begin();
        obj_id_t vid = victim->id;
        freq_set.erase(victim);
        lookup.erase(vid);
        return vid;
    }
};
