#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// Hybrid LFU-LRU: Evict by freq, break ties by recency only (LRU among lowest freq)
// (Removes size as tiebreaker to improve recency-awareness on short/medium workloads)
class CacheManager {
private:
    struct LFUNode {
        uint64_t freq;
        uint64_t timestamp;
        obj_id_t obj_id;
        LFUNode(uint64_t f, uint64_t ts, obj_id_t id) : freq(f), timestamp(ts), obj_id(id) {}
        // Evict least freq, then oldest
        bool operator<(const LFUNode& o) const {
            if (freq != o.freq) return freq < o.freq;
            if (timestamp != o.timestamp) return timestamp < o.timestamp;
            return obj_id < o.obj_id;
        }
    };
    std::unordered_map<obj_id_t, std::pair<LFUNode, std::set<LFUNode>::iterator>> map;
    std::set<LFUNode> lfu_set;
    uint64_t time_counter = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = map.find(obj_id);
        if (it == map.end()) return false;
        LFUNode node = it->second.first;
        lfu_set.erase(it->second.second);
        node.freq += 1;
        node.timestamp = ++time_counter;
        auto iter = lfu_set.insert(node).first;
        it->second = std::make_pair(node, iter);
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        (void)obj_size; // Not used in hybrid LFU-LRU
        auto it = map.find(obj_id);
        if (it != map.end()) {
            find(obj_id);
            return;
        }
        LFUNode node(1, ++time_counter, obj_id);
        auto iter = lfu_set.insert(node).first;
        map[obj_id] = std::make_pair(node, iter);
    }

    obj_id_t evict() {
        if (lfu_set.empty()) return 0;
        auto it = lfu_set.begin();
        obj_id_t victim = it->obj_id;
        map.erase(victim);
        lfu_set.erase(it);
        return victim;
    }
};
