#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// Simple LFU cache: evict least-frequently-used object (break ties with oldest)
class CacheManager {
private:
    struct Node {
        uint64_t freq;
        uint64_t timestamp;
        obj_id_t obj_id;
        Node(uint64_t f, uint64_t ts, obj_id_t oid)
            : freq(f), timestamp(ts), obj_id(oid) {}
        bool operator<(const Node &other) const {
            if (freq != other.freq)
                return freq < other.freq;
            if (timestamp != other.timestamp)
                return timestamp < other.timestamp;
            return obj_id < other.obj_id;
        }
    };
    std::unordered_map<obj_id_t, std::pair<uint64_t, std::set<Node>::iterator>> table; // obj_id -> {size, iterator}
    std::set<Node> freqset;
    uint64_t global_clock = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = table.find(obj_id);
        if (it == table.end())
            return false;
        // Remove from set and update frequency
        auto node_it = it->second.second;
        Node new_node = *node_it;
        freqset.erase(node_it);
        new_node.freq += 1;
        new_node.timestamp = ++global_clock;
        auto inserted = freqset.insert(new_node);
        it->second.second = inserted.first;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        if (table.count(obj_id)) {
            find(obj_id);
            return;
        }
        Node node(1, ++global_clock, obj_id);
        auto it = freqset.insert(node).first;
        table[obj_id] = {obj_size, it};
    }

    obj_id_t evict() {
        if (freqset.empty())
            return 0;
        auto node_it = freqset.begin();
        obj_id_t victim = node_it->obj_id;
        freqset.erase(node_it);
        table.erase(victim);
        return victim;
    }
};
