#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

class CacheManager {
private:
    // GDSF with frequency aging: track frequency and size for each object.
    struct CacheObject {
        uint64_t freq;
        uint64_t size;
        uint64_t timestamp;
        std::set<std::tuple<double, uint64_t, obj_id_t>>::iterator pqit;
    };
    // Priority queue node: tuple for easy erase and reinsert
    // (priority, timestamp, obj_id)
    std::set<std::tuple<double, uint64_t, obj_id_t>> priority_set;
    std::unordered_map<obj_id_t, CacheObject> cache_map;
    uint64_t counter = 0;
    double last_evict_pri = 0.0;

    // Slightly decrease freq on every eviction to "age" frequency counts
    // Remove frequency aging for simplicity and to boost recency bias
    // This avoids global rescoring that can cause massive churn.
    void age_frequencies() {
        // No-op: remove explicit frequency aging
    }

    // Add mild recency tiebreaking and bias for small objects in priority calculation
    double calc_pri(uint64_t freq, uint64_t size) {
        double small_bonus = (size <= 8) ? 1.0 : 0.0;
        // Add slight recency component for recency ties
        if (size == 0) return last_evict_pri + (freq + small_bonus) * 1e6 + counter * 1e-4;
        return last_evict_pri + (freq + small_bonus) * 1e6 / size + counter * 1e-4;
    }

public:
    bool find(obj_id_t obj_id) {
        auto it = cache_map.find(obj_id);
        if (it == cache_map.end()) return false;
        priority_set.erase(it->second.pqit);
        // Mildly favor small objects on hit, give them a bigger frequency boost
        if (it->second.size <= 8)
            it->second.freq += 2;
        else
            it->second.freq += 1;
        it->second.timestamp = ++counter;
        double new_pri = calc_pri(it->second.freq, it->second.size);
        auto pqit = priority_set.insert({new_pri, it->second.timestamp, obj_id}).first;
        it->second.pqit = pqit;
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = cache_map.find(obj_id);
        if (it != cache_map.end()) {
            find(obj_id);
            return;
        }
        uint64_t freq = (obj_size <= 8) ? 2 : 1; // Slightly favor tiny objects on insert
        uint64_t ts = ++counter;
        double pri = calc_pri(freq, obj_size);
        auto pqit = priority_set.insert({pri, ts, obj_id}).first;
        cache_map[obj_id] = {freq, obj_size, ts, pqit};
    }

    obj_id_t evict() {
        if (priority_set.empty()) return 0;
        auto it = priority_set.begin();
        obj_id_t victim = std::get<2>(*it);
        last_evict_pri = std::get<0>(*it);
        priority_set.erase(it);
        cache_map.erase(victim);
        // No global frequency aging or rescoring
        return victim;
    }
};
