#include <cstdint>
#include <unordered_map>
#include <set>

typedef uint64_t obj_id_t;

// LFU+Size-Aware: Uses frequency, but for ties, prefers to evict LARGER objects (so we keep small hot objects).
class CacheManager {
private:
    struct LFUNode {
        uint64_t freq;
        uint64_t timestamp; // for LRU tiebreak
        uint64_t size;
        obj_id_t obj_id;
        LFUNode(uint64_t f, uint64_t ts, uint64_t s, obj_id_t id) : freq(f), timestamp(ts), size(s), obj_id(id) {}
        // Set ordering: freq, then size descending (evict big on tie), then timestamp, then obj_id
        bool operator<(const LFUNode &o) const {
            if (freq != o.freq) return freq < o.freq;
            if (size != o.size) return size > o.size; // prefer to evict bigger object on tie
            if (timestamp != o.timestamp) return timestamp < o.timestamp;
            return obj_id < o.obj_id;
        }
    };

    std::unordered_map<obj_id_t, std::set<LFUNode>::iterator> obj2iter;
    std::set<LFUNode> lfu_set;
    std::unordered_map<obj_id_t, uint64_t> obj2size;
    uint64_t time_counter = 0;

public:
    bool find(obj_id_t obj_id) {
        auto it = obj2iter.find(obj_id);
        if (it == obj2iter.end()) return false;
        auto node = *(it->second);
        lfu_set.erase(it->second);
        node.freq += 1;
        node.timestamp = ++time_counter;
        auto iter = lfu_set.insert(LFUNode(node.freq, node.timestamp, node.size, node.obj_id)).first;
        obj2iter[obj_id] = iter;
        obj2size[obj_id] = node.size; // ensure consistency if ever updated
        return true;
    }

    void insert(obj_id_t obj_id, uint64_t obj_size) {
        auto it = obj2iter.find(obj_id);
        if (it != obj2iter.end()) {
            find(obj_id);
            return;
        }
        LFUNode node(1, ++time_counter, obj_size, obj_id);
        auto iter = lfu_set.insert(node).first;
        obj2iter[obj_id] = iter;
        obj2size[obj_id] = obj_size;
    }

    obj_id_t evict() {
        if (lfu_set.empty()) return 0;
        auto it = lfu_set.begin();
        obj_id_t victim = it->obj_id;
        lfu_set.erase(it);
        obj2iter.erase(victim);
        obj2size.erase(victim);
        // Remove frequency aging: pure LFU with size tiebreaker
        return victim;
    }
};
